mapper.select:
select
  id, account_id, priority_id, gmt_create, gmt_modified
from eshop.auth_account_priority_relationship


        and id = #{id}


        and account_id = #{accountId}


        and priority_id = #{priorityId}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}
